// src/pages/Login.jsx
import React, { useState, useContext, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import {
  loginLocal as apiLoginLocal,
  loginGoogle as apiLoginGoogle
} from '../services/api'
import { AppContext } from '../context/AppContext'
import { GoogleLogin } from '@react-oauth/google'

export default function Login() {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [error, setError] = useState('')
  const [loading, setLoading] = useState(false)

  const { login, token } = useContext(AppContext)
  const navigate = useNavigate()

  // Si ya estamos logueados, redirige inmediatamente a Home
  useEffect(() => {
    if (token) {
      navigate('/', { replace: true })
    }
  }, [token, navigate])

  // Login con email/contraseÃ±a
  const handleLocal = async (e) => {
    e.preventDefault()
    setError('')
    setLoading(true)

    try {
      const { data } = await apiLoginLocal({ email, password })
      const jwt = data.access_token
      login(jwt)
      // navigate se dispara en el useEffect
    } catch {
      setError('ðŸš« Credenciales invÃ¡lidas')
    } finally {
      setLoading(false)
    }
  }

  // Login con Google
  const handleGoogleSuccess = async ({ credential }) => {
    setError('')
    setLoading(true)

    if (!credential) {
      setError('ðŸš« No se recibiÃ³ token de Google')
      setLoading(false)
      return
    }

    try {
      const { data } = await apiLoginGoogle(credential)
      // el backend debe devolver access_token como en loginLocal
      const jwt = data.access_token
      login(jwt)
      // navigate se dispara en el useEffect
    } catch {
      setError('ðŸš« Login con Google fallido')
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="relative max-w-sm mx-auto p-4">
      {loading && (
        <div className="absolute inset-0 bg-black bg-opacity-50 flex flex-col items-center justify-center rounded">
          <div className="w-12 h-12 border-4 border-white border-t-transparent rounded-full animate-spin" />
          <p className="text-white mt-3">Iniciando sesiÃ³nâ€¦</p>
        </div>
      )}

      <h1 className="text-2xl font-semibold mb-4">Iniciar SesiÃ³n</h1>

      {error && <p className="text-red-600 mb-2">{error}</p>}

      <form onSubmit={handleLocal} className="space-y-4">
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          className="w-full border p-2 rounded"
          disabled={loading}
          required
        />
        <input
          type="password"
          placeholder="ContraseÃ±a"
          value={password}
          onChange={e => setPassword(e.target.value)}
          className="w-full border p-2 rounded"
          disabled={loading}
          required
        />
        <button
          type="submit"
          disabled={loading}
          className="w-full bg-blue-600 text-white p-2 rounded hover:bg-blue-700 disabled:opacity-60 flex items-center justify-center"
        >
          {loading ? (
            <>
              <svg
                className="animate-spin h-5 w-5 mr-2 text-white"
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
              >
                <circle
                  className="opacity-25"
                  cx="12"
                  cy="12"
                  r="10"
                  stroke="currentColor"
                  strokeWidth="4"
                />
                <path
                  className="opacity-75"
                  fill="currentColor"
                  d="M4 12a8 8 0 018-8v4l3-3-3-3v4a8 8 0 00-8 8z"
                />
              </svg>
              Iniciandoâ€¦
            </>
          ) : (
            'Entrar'
          )}
        </button>
      </form>

      <div className="mt-6 flex justify-center">
        <GoogleLogin
          onSuccess={handleGoogleSuccess}
          onError={() => setError('ðŸš« Google Sign-In fallÃ³')}
          disabled={loading}
        />
      </div>
    </div>
  )
}
